import { MDBDropdown, MDBDropdownMenu, MDBDropdownToggle, MDBDropdownItem } from 'mdb-react-ui-kit';

import React,{ useState } from 'react';
import {
  CitySelect,
  CountrySelect,
  StateSelect,

} from "react-country-state-city";

import "react-country-state-city/dist/react-country-state-city.css";

function Countrys() {
  const [countryid, setCountryid] = useState(0);
  const [stateid, setstateid] = useState(0);
  return (
    <div>
      <h6>Country</h6>
      <CountrySelect
        onChange={(e) => {
          setCountryid(e.id);
        }}
        placeHolder="Select Country"
      />
      <h6>State</h6>
      <StateSelect
        countryid={countryid}
        onChange={(e) => {
          setstateid(e.id);
        }}
        placeHolder="Select State"
      />
      <h6>City</h6>
      <CitySelect
        countryid={countryid}
        stateid={stateid}
        onChange={(e) => {
          console.log(e);
        }}
        placeHolder="Select City"
      />

      
      <h1>After Change</h1>
      <br></br>
      <br></br>
      <br></br>

      <MDBDropdown group>
        <MDBDropdownToggle color='primary'> <CountrySelect
        onChange={(e) => {
          setCountryid(e.id);
        }}
        placeHolder="Select Country"
      /></MDBDropdownToggle>
        <MDBDropdownMenu>
          
        </MDBDropdownMenu>
      </MDBDropdown>
      <MDBDropdown group></MDBDropdown>
      
    </div>
  );
}

export default Countrys
