import {
  CitySelect,
  CountrySelect,
  StateSelect,

} from "react-country-state-city";

import "react-country-state-city/dist/react-country-state-city.css";

function Countrys() {
  const [countryid, setCountryid] = useState(0);

  const [address, setSAdress] = useState({
    country:"",
    state:"",
    city:""

  });

  const [stateid, setstateid] = useState(0);

const handlechange=(e)=>{

  console.log(e)
    var copyOfLead = { ...address };
    copyOfLead[e.target.name] = e.target.value;
    //set the form values
    setSAdress(copyOfLead);

   // console.log(address)

}

  return (
    <div>
      {/* <h6>Country</h6>
      <CountrySelect
        onChange={(e) => {
