        }}
        placeHolder="Select Country"
      />
      <h6>State</h6>
      <StateSelect
        countryid={countryid}
        onChange={(e) => {
          setstateid(e.id);
        }}
        placeHolder="Select State"
      />
      <h6>City</h6>
      <CitySelect
        countryid={countryid}
        stateid={stateid}
        onChange={(e) => {
          console.log(e);
        }}
        placeHolder="Select City"
      /> */}

      
      <h1>After Change</h1>
      <br></br>
      <br></br>
      <br></br>

      <MDBDropdown group >
        <CountrySelect
       // value ='country'
        onChange={(e) => {
          setCountryid(e.id);
          console.log(e)

         handlechange(e)
        }}
        
        placeHolder="Select Country"
      /> 
      </MDBDropdown>


      <MDBDropdown group name='state' onClick={(e)=>{handlechange(e)}}>
      <StateSelect
        countryid={countryid}
        onChange={(e) => {
          setstateid(e.id);
         // handlechange(e);
        }}
        
        placeHolder="Select State"
      />
      </MDBDropdown>

      <MDBDropdown group name='city' onClick={(e)=>{handlechange(e)}}>
      <CitySelect
        countryid={countryid}
        stateid={stateid}
        onChange={(e) => {
          console.log(e);
          
        }}
        
        
        placeHolder="Select City"
      />
      
      </MDBDropdown>



      
      
    </div>
  );
}

export default Countrys
