import { useEffect, useState } from "react";
import {useNavigate} from "react-router-dom";


import {
  MDBValidation,
  MDBValidationItem,
  MDBInput,
  MDBInputGroup,
  MDBBtn,
  MDBCheckbox
} from 'mdb-react-ui-kit';


function Update() {


  const [lead, setLead] = useState({
      fname:"",
      lname:"",
      c_name:"",
      c_number:"",
      comment:"",
      country:"",
      created_by:"",
      email:"",
      interest:"",
      lead_source:"",
      lead_type:""
     
  });

  var navigate = useNavigate();

  // const handleChange = (e) => {
  //   var copyOfLead = { ...lead };
  //   copyOfLead[e.target.id] = e.target.value;
  //   //set the form values
  //   setLead(copyOfLead);
  // };

  useEffect(()=>{
      
    const user_id=sessionStorage.getItem("id");

    var helper = new XMLHttpRequest();

    
    const url="http://localhost:8080/user/lead"+user_id

    helper.onreadystatechange=()=>{

  
        if(helper.readyState === 4 )
        {
          console.log(helper);
            var result=JSON.parse(helper.responseText);
            
            setLead(result)
            console.log(lead)
            
        }
    }

  
    helper.open("GET",url)
    helper.setRequestHeader("Content-Type", "application/json")
    helper.send()

    

  },[]);

  // const submit=()=>{
    
    
  //   var userObjectInStringFormat = JSON.stringify(lead);
  
  //   console.log(userObjectInStringFormat);
    
  //   var helper = new XMLHttpRequest();
  //   helper.onreadystatechange=()=>{
      
  //     if(helper.readyState==4 && helper.status==201)
  //     {
   
      
  //       var result=JSON.parse(helper.responseText);
  //        if(result!=null)
  //       {
  //         alert("Registation done...!!!")
  //        navigate("/")
  //       }
  
  //     }
  
      
  // };
  // helper.open("POST","http://localhost:8080/user/lead");
  
  // helper.setRequestHeader("content-type","application/json")
  
  // helper.send(userObjectInStringFormat);
  // };


  




    return (
      <>
      <MDBValidation className='row g-3'>
      <MDBValidationItem className='col-md-4'>
        <MDBInput
          value={formValue.fname}
          name='fname'
          onChange={onChange}
          id='validationCustom01'
          required
          label='First name'
        />
      </MDBValidationItem>
      <MDBValidationItem className='col-md-4'>
        <MDBInput
          value={formValue.lname}
          name='lname'
          onChange={onChange}
          id='validationCustom02'
          required
          label='Last name'
        />
      </MDBValidationItem>
      <MDBValidationItem feedback='Please choose a username.' invalid className='col-md-4'>
        <MDBInputGroup textBefore='@'>
          <input
            type='text'
            className='form-control'
            id='validationCustomUsername'
            placeholder='Username'
            required
          />
        </MDBInputGroup>
      </MDBValidationItem>
      <MDBValidationItem className='col-md-6' feedback='Please provide a valid city.' invalid>
        <MDBInput
          value={formValue.city}
          name='city'
          onChange={onChange}
          id='validationCustom03'
          required
          label='City'
        />
      </MDBValidationItem>
      <MDBValidationItem className='col-md-6' feedback='Please provide a valid zip.' invalid>
        <MDBInput
          value={formValue.zip}
          name='zip'
          onChange={onChange}
          id='validationCustom05'
          required
          label='Zip'
        />
      </MDBValidationItem>
      <MDBValidationItem className='col-12' feedback='You must agree before submitting.' invalid>
        <MDBCheckbox label='Agree to terms and conditions' id='invalidCheck' required />
      </MDBValidationItem>
      <div className='col-12'>
        <MDBBtn type='submit'>Submit form</MDBBtn>
        <MDBBtn type='reset'>Reset form</MDBBtn>
      </div>
    </MDBValidation>
  );
      
      
      </>
    );
  }
  
  export default Update;
